import React, { Component } from "react";
import Form from "react-bootstrap/Form";
import { Redirect, Link } from "react-router-dom";
import axios from "axios";

import LinkInClass from "./LinkInClass";
// import ProductTableRow from "./ProductTableRow";
// import Images from "./Images";
import {
  ACCESS_LEVEL_NORMAL_USER,
  SERVER_HOST,
} from "../config/global_constants";

export default class EditProduct extends Component {
  constructor(props) {
    super(props);

    this.state = {
      productName: ``,
      productDescription: ``,
      year: ``,
      price: ``,
      quantity: ``,
      images: [],
      selectedFiles: null,
      redirectToDisplayAllProducts:
        localStorage.accessLevel < ACCESS_LEVEL_NORMAL_USER,
      wasSubmittedAtLeastOnce: false,
    };
    console.log(this.state.images);
  }

  componentDidMount() {
    this.inputToFocus.focus();

    axios
      .get(`${SERVER_HOST}/products/${this.props.match.params.id}`, {
        headers: { authorization: localStorage.token },
      })
      .then((res) => {
        if (res.data) {
          if (res.data.errorMessage) {
            console.log(res.data.errorMessage);
          } else {
            this.setState({
              productName: res.data.productName,
              productDescription: res.data.productDescription,
              year: res.data.year,
              price: res.data.price,
              quantity: res.data.quantity,
              images: res.data.photos,
            });
            console.log(this.state.images);
            console.log(res.data.photos);

            this.state.images.map((photo) => {
              return axios
                .get(`${SERVER_HOST}/products/photo/${photo.filename}`)
                .then((res) => {
                  console.log(res.data.image);

                  if (res.data) {
                    if (res.data.errorMessage) {
                      console.log(res.data.errorMessage);
                    } else {
                      document.getElementById(
                        photo._id
                      ).src = `data:;base64,${res.data.image}`;
                    }
                  } else {
                    console.log("Record not found");
                  }
                })
                .catch((error) => {
                  console.log(error);
                });
            });
          }
        } else {
          console.log("Record not found");
        }
      });
  }

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  handleFileChange = (e) => {
    this.setState({ selectedFiles: e.target.files });
  };

  // handleSubmit = (e) => {
  //   e.preventDefault();

  //   const productObject = {
  //     productName: this.state.productName,
  //     productDescription: this.state.productDescription,
  //     year: this.state.year,
  //     price: this.state.price,
  //     quantity: this.state.quantity,
  //     images: this.state.images,
  //   };

  //   axios
  //     .put(
  //       `${SERVER_HOST}/products/${this.props.match.params.id}`,
  //       productObject,
  //       {
  //         headers: { authorization: localStorage.token },
  //       }
  //     )
  //     .then((res) => {
  //       this.setState({ redirectToDisplayAllProducts: true });
  //     })
  //     .catch((err) => {
  //       this.setState({ wasSubmittedAtLeastOnce: true });
  //     });
  // };

  handleSubmit = (e) => {
    e.preventDefault();

    if(Object.keys(formInputState).every(index => formInputState[index])){}
    let formData = new FormData();
    formData.append("productName", this.state.productName);
    formData.append("productDescription", this.state.productDescription);
    formData.append("year", this.state.year);
    formData.append("price", this.state.price);
    formData.append("quantity", this.state.quantity);

    if (this.state.selectedFiles) {
      for (let i = 0; i < this.state.selectedFiles.length; i++) {
        formData.append("productPhotos", this.state.selectedFiles[i]);
      }
    }

    axios
      .post(`${SERVER_HOST}/products`, formData, {
        headers: {
          authorization: localStorage.token,
          "Content-type": "multipart/form-data",
        },
      })
      .then((res) => {
        this.setState({ redirectToDisplayAllProducts: true });
      })
      .catch((err) => {
        this.setState({ wasSubmittedAtLeastOnce: true });
      });
  };

  render() {
    let errorMessage = "";
    if (this.state.wasSubmittedAtLeastOnce) {
      errorMessage = (
        <div className="error">
          Error: All fields must be filled in
          <br />
        </div>
      );
    }

    return (
      <div className="form-container">
        {this.state.redirectToDisplayAllProducts ? (
          <Redirect to="/DisplayAllProducts" />
        ) : null}

        {this.state.images.map((image) => (
          <img src={image._id} id={image._id} key={image._id} alt="image" />
        ))}

        {errorMessage}

        <Form>
          <Form.Group controlId="productName">
            <Form.Label>Product Name</Form.Label>
            <Form.Control
              ref={(input) => {
                this.inputToFocus = input;
              }}
              type="text"
              name="productName"
              value={this.state.productName}
              onChange={this.handleChange}
            />
          </Form.Group>
          {/* <div className="product-image">
            {this.state.images.map((image) => (
              <img id={image} alt="" />
            ))}
          </div> */}
          <Form.Group controlId="productDescription">
            <Form.Label>Product Description</Form.Label>
            <Form.Control
              type="text"
              name="productDescription"
              value={this.state.productDescription}
              onChange={this.handleChange}
            />
          </Form.Group>
          <Form.Group controlId="year">
            <Form.Label>Year</Form.Label>
            <Form.Control
              type="text"
              name="year"
              value={this.state.year}
              onChange={this.handleChange}
            />
          </Form.Group>
          <Form.Group controlId="price">
            <Form.Label>Price</Form.Label>
            <Form.Control
              type="text"
              name="price"
              value={this.state.price}
              onChange={this.handleChange}
            />
          </Form.Group>
          <Form.Group controlId="quantity">
            <Form.Label>Quantity</Form.Label>
            <Form.Control
              type="text"
              name="quantity"
              value={this.state.quantity}
              onChange={this.handleChange}
            />
          </Form.Group>
          {/* {this.props.cars.map((car) => (
            <CarTableRow key={car._id} car={car} />
          ))} */}
          {/* {this.props.car.photos.map((photo) => (
            <img
              className="product-image"
              key={photo._id}
              id={photo._id}
              alt="Image"
            />
          ))}  */}
          <Form.Group controlId="photos">
            <Form.Label>Photos</Form.Label>
            <Form.Control
              type="file"
              name="photos"
              multiple
              onChange={this.handleFileChange}
            />
          </Form.Group>{" "}
          <br />
          <br />
          <LinkInClass
            value="Update"
            className="green-button"
            onClick={this.handleSubmit}
          />
          <Link className="red-button" to={"/DisplayAllCars"}>
            Cancel
          </Link>
        </Form>
      </div>
    );
  }
}
