import React, { Component } from "react";
import { Link } from "react-router-dom";

import axios from "axios";

import CarTable from "./CarTable";

import Header from "./Header";
import Footer from "./Footer";
//import Product from "./Product";

import {
  // ACCESS_LEVEL_GUEST,
  ACCESS_LEVEL_ADMIN,
  SERVER_HOST,
} from "../config/global_constants";

export default class DisplayAllCars extends Component {
  constructor(props) {
    super(props);

    this.state = {
      cars: [],
      appTitle: "Customers",
      customersCount: 0,
      users: [],
      customers: [
        { id: 1, name: "Scott", phone: "234234" },
        { id: 2, name: "Jones", phone: "567567" },
        { id: 3, name: "Smith", phone: "890890" },
        { id: 4, name: "John", phone: "3453466" },
      ],
    };
  }

  componentDidMount() {
    axios
      .get(`${SERVER_HOST}/cars`)
      .then((res) => {
        this.setState({ cars: res.data });
      })
      .catch((err) => {
        // do nothing
      });
    axios
      .get(`${SERVER_HOST}/users`)
      .then((res) => {
        this.setState({ users: res.data });
        console.log(this.res.data);
      })
      .catch((err) => {
        // do nothing
      });
  }
  //Executes when user clicks on Refresh button
  onRefreshClick = () => {
    this.setState({
      customersCount: 7,
    });
  };

  render() {
    return (
      <div className="outer__container">
        <Header />

        <h4>{this.state.appTitle} </h4>
        <span className="badge badge-secondary m-2">
          {this.state.customersCount}
        </span>
        <button className="btn btn-info" onClick={() => this.onRefreshClick()}>
          Refresh
        </button>
        <div className="container">
          <table className="table">
            <thead>
              <tr key="{cust.id}">
                <th>#</th>
                <th>Customer Name</th>
                <th>Phone</th>
              </tr>
              <tbody>
                {this.state.customers.map((cust) => {
                  return (
                    <tr>
                      <td>{cust.id}</td>
                      <td>{cust.name}</td>
                      <td>{cust.phone}</td>
                    </tr>
                  );
                })}
              </tbody>
            </thead>
          </table>
          <CarTable cars={this.state.cars} />
        </div>
        {localStorage.accessLevel >= ACCESS_LEVEL_ADMIN ? (
          <div className="add-new-car">
            <Link className="blue-button" to={"/AddCar"}>
              Add New Car
            </Link>
            <Link className="header__link" to={"/ResetDatabase"}>
              <div className="header__option">
                <span className="header__optionLineOne"></span>
                <span className="blue-button">Reset Database</span>
              </div>
            </Link>{" "}
          </div>
        ) : null}

        <Footer />
      </div>
    );
  }
}

