import React, { Component } from "react";
import { Link } from "react-router-dom";
import "../css/App.css";
import axios from "axios";
import SearchIcon from "@material-ui/icons/Search";
import ShoppingBasketIcon from "@material-ui/icons/ShoppingBasket";
import emazon from "../images/logowhite.png";
import {
  ACCESS_LEVEL_GUEST,
  // ACCESS_LEVEL_ADMIN,
  SERVER_HOST,
} from "../config/global_constants";
import Logout from "./Logout";

export default class Header extends Component {
  constructor(props) {
    super(props);

    this.state = {
      products: [],
      appTitle: "Customers",
      searchText: "",
      placeholder: "Search by Name, Price, Year...",
    };
  }

  componentDidMount() {
    axios.get(`${SERVER_HOST}/products`).then((res) => {
      console.log(res.data);

      /*Filter by Year*/

      let releaseYears = res.data.map((album) => album.releaseYear);
      let uniqueYears = [...new Set(releaseYears)].sort();
      uniqueYears.unshift("All Years");
      console.log("uniqueYears");

      /*Filter by Label*/

      let labels = res.data.map((album) => album.label);
      let uniqueLabels = [...new Set(labels)].sort();
      uniqueLabels.unshift("All Labels");
      console.log("uniqueLabels");

      /*Filter by Artist*/

      let artists = res.data.map((album) => album.artist);
      let uniqueArtists = [...new Set(artists)].sort();
      uniqueArtists.unshift("All Artists");
      console.log("uniqueArtists");

      if (res.data) {
        if (res.data.errorMessage) {
          console.log(res.data.errorMessage);
        } else {
          console.log("Records read");
          this.setState({
            albums: res.data,
            selectedAlbums: res.data,
            releaseYears: uniqueYears,
            labels: uniqueLabels,
            artists: uniqueArtists,
          });
        }
      } else {
        console.log("Record not found");
      }
    });
  }

  filterYear = (e) => {
    if (e.target.value === this.state.selectedYears) {
      this.setState({ selectedAlbums: this.state.albums });
    } else {
      this.setState({
        selectedAlbums: this.state.albums.filter(
          (product) => product.releaseYear.toString() === e.target.value
        ),
      });
    }
  };

  filterArtist = (e) => {
    if (e.target.value === this.state.selectedArtists) {
      this.setState({ selectedAlbums: this.state.albums });
    } else {
      this.setState({
        selectedAlbums: this.state.albums.filter(
          (product) => product.artist.toString() === e.target.value
        ),
      });
    }
  };

  filterLabel = (e) => {
    if (e.target.value === this.state.selectedLabel) {
      this.setState({ selectedAlbums: this.state.albums });
    } else {
      this.setState({
        selectedAlbums: this.state.albums.filter(
          (product) => product.label.toString() === e.target.value
        ),
      });
    }
  };

  handleSearch = (searchTag) => {
    this.setState({
      selectedAlbums: this.state.albums.filter(
        (album) =>
          album.title.toLowerCase().includes(searchTag.toLowerCase()) ||
          album.artist.toLowerCase().includes(searchTag.toLowerCase()) ||
          album.label.toLowerCase().includes(searchTag.toString())
      ),
    });
  };

  render() {
    const { products, searchText } = this.state;
    console.log(searchText);

    const filteredProducts = products.filter((products) => {
      if (searchText === "") {
        return products;
      } else if (
        products.productName.toLowerCase().includes(searchText.toLowerCase()) ||
        products.productDescription
          .toLowerCase()
          .includes(searchText.toLowerCase()) ||
        products.year == searchText
      ) {
        return products;
      }
    });

    return (
      <nav className="header">
        <Link to="/">
          <img src={emazon} className="header__logo" alt="Logo" />
        </Link>
        <div className="header__search">
          <input
            className="header__searchInput"
            type="text"
            placeholder="Search by prduct name or size..."
            handleChange={(e) => this.setState({ searchText: e.target.value })}
          />
          <SearchIcon className="header__searchIcon" />
        </div>
        <div className="header__nav">
          <Link to="/login" className="header__link">
            <div className="header__option">
              <span className="header__optionLineOne">
                {/* Hello {user ? user : "Stranger"} */}
              </span>
              <span className="header__optionLineTwo">
                {/* Sign {user ? "Out" : "In"} */}
              </span>
            </div>
          </Link>
          <Link className="header__link" to={"/Login"}>
            <div className="header__option">
              <span className="header__optionLineOne"></span>

              {localStorage.accessLevel > ACCESS_LEVEL_GUEST ? (
                <div className="header__optionTwo">
                  {localStorage.profilePhoto !== "null" ? (
                    <img
                      id="profilePhoto"
                      src={`data:;base64,${localStorage.profilePhoto}`}
                      alt=""
                    />
                  ) : null}
                  <Logout />
                  <p style={{ color: "red" }} className="">
                    Admin
                  </p>
                </div>
              ) : (
                <span className="header__optionLineTwo">Login</span>
              )}
            </div>
          </Link>
          <Link className="header__link" to={"/Register"}>
            <div className="header__option">
              <span className="header__optionLineOne"></span>
              {localStorage.accessLevel > ACCESS_LEVEL_GUEST ? (
                <div className="header__optionTwo">
                  {localStorage.profilePhoto !== "null" ? (
                    <img
                      id="profilePhoto"
                      src={`data:;base64,${localStorage.profilePhoto}`}
                      alt=""
                    />
                  ) : null}
                </div>
              ) : (
                <span className="header__optionLineTwo">Sign Up</span>
              )}
            </div>
          </Link>

          <Link to="/" className="header__link">
            <div className="header__option">
              <span className="header__optionLineOne">Returns</span>
              <span className="header__optionLineTwo">& Orders</span>
            </div>
          </Link>
          <Link to="/checkout" className="header__link">
            <div className="header__optionBasket">
              <ShoppingBasketIcon />
              <span className="header__basketCount header__optionLineTwo">
                {/* {basket?.length} */}
              </span>
            </div>
          </Link>
        </div>
      </nav>
    );
  }
}
