import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import CarTable from "./CarTable";
import Header from "./Header";
import Footer from "./Footer";
import ShoppingCart from "./ShoopingCart";
import SearchIcon from "@material-ui/icons/Search";
//import Product from "./Product";

import {
  // ACCESS_LEVEL_GUEST,
  ACCESS_LEVEL_ADMIN,
  SERVER_HOST,
} from "../config/global_constants";

export default class DisplayAllCars extends Component {
  constructor(props) {
    super(props);

    this.state = {
      cars: [],
      appTitle: "Customers",
      customersCount: 0,
      users: [],
      searchText: "",
      customers: [
        {
          id: 1,
          name: "Scott",
          phone: "234234",
          address: { city: "London" },
          photo: "https://picsum.photos/id/1010/60",
        },
        {
          id: 2,
          name: "Jones",
          phone: "567567",
          address: { city: "Praga" },
          photo: "https://picsum.photos/id/1011/60",
        },
        {
          id: 3,
          name: "Smith",
          phone: null,
          address: { city: "Sofia" },
          photo: "https://picsum.photos/id/1012/60",
        },
        {
          id: 4,
          name: "John",
          phone: "3453466",
          address: { city: "Plovdiv" },
          photo: "https://picsum.photos/id/1013/60",
        },
        {
          id: 5,
          name: "Alan",
          phone: "3453466",
          address: { city: "Castlebalyney" },
          photo: "https://picsum.photos/id/1014/60",
        },
      ],
    };
  }

  componentDidMount() {
    axios
      .get(`${SERVER_HOST}/cars`)
      .then((res) => {
        this.setState({ cars: res.data });
      })
      .catch((err) => {
        // do nothing
      });
  }
  //Executes when user clicks on Refresh button
  onRefreshClick = () => {
    this.setState({
      customersCount: 7,
    });
  };
  getPhoneToRender = (phone) => {
    if (phone) {
      return phone;
    } else {
      return <div className="bg-warning p-2 text-center">No phone</div>;
    }
  };

  customerNameStyle = (custName) => {
    if (custName.startsWith("S")) return "green-highlight border-left";
    else if (custName.startsWith("J")) return "red-highlight border-right";
    else return { fontWeight: 600 };
  };

  //pnclick button receives customer object & index of the clicked customer
  onChangePicClick = (cust, index) => {
    //get existing customer
    var custArray = this.state.customers;
    custArray[index].photo = "https://picsum.photos/id/1016/60";
    //update customer array
    this.setState({ customers: custArray });
  };

  getCustomerRow = () => {
    return this.state.customers.map((cust, index) => {
      return (
        <tr key={cust.id}>
          <td>{cust.id}</td>
          <td>
            <img src={cust.photo} alt="Customer" />
            <div>
              <button
                className="btn btn-sm btn-secondary"
                onClick={() => {
                  this.onChangePicClick(cust, index);
                }}
              >
                Change Pic
              </button>
            </div>
          </td>
          <td className={this.customerNameStyle(cust.name)}>{cust.name}</td>
          <td>{this.getPhoneToRender(cust.phone)}</td>
          <td>{cust.address.city}</td>
        </tr>
      );
    });
  };
  render() {
    const { cars, searchText } = this.state;
    // console.log(searchText)

    const filteredCars = cars.filter((cars) => {
      if (searchText === "") {
        return cars;
      } else if (
        cars.model.toLowerCase().includes(searchText.toLowerCase()) ||
        cars.year.toLowerCase().includes(searchText.toLowerCase()) ||
        cars.year == searchText
      ) {
        return cars;
      }
    });
    return (
      <div className="outer__container">
        <Header />
        <div className="header__search">
          <input
            className="header__searchInput"
            type="text"
            handleChange={(e) => this.setState({ searchText: e.target.value })}
          />
          <SearchIcon className="header__searchIcon" type="submit" onClick="" />
        </div>
        <ShoppingCart x="10" />
        {/* <h4>{this.state.appTitle} </h4>
        <span className="badge badge-secondary m-2">
          {this.state.customersCount}
        </span>
        <button className="btn btn-info" onClick={() => this.onRefreshClick()}>
          Refresh
        </button> */}
        <table className="table">
          <thead>
            <tr>
              <th>id</th>
              <th>Customer Name</th>
              <th>Phone</th>
              <th>City</th>
            </tr>
          </thead>
          <tbody>{this.getCustomerRow()}</tbody>
        </table>
        <div className="container">
          <CarTable cars={this.state.cars} />
        </div>
        {localStorage.accessLevel >= ACCESS_LEVEL_ADMIN ? (
          <div className="add-new-car">
            <Link className="blue-button" to={"/AddCar"}>
              Add New Car
            </Link>
            <Link className="header__link" to={"/ResetDatabase"}>
              <div className="header__option">
                <span className="header__optionLineOne"></span>
                <span className="blue-button">Reset Database</span>
              </div>
            </Link>{" "}
          </div>
        ) : null}

        <Footer />
      </div>
    );
  }
}

