  //form validate start
  validateProductName() {
    const pattern = /^[0-9a-zA-Z !@%£$?+\s]+$/;
    return pattern.test(String(this.state.productName));
  }

  validateProductDescription() {
    const pattern = /^[0-9a-zA-Z !@%£$?,.+\s]+$/;
    return pattern.test(String(this.state.productDescription));
  }
  validateYear() {
    const pattern = /^[0-9]+$/;
    const year = parseInt(this.state.year);

     return pattern.test(String(year)) && year >= 1950 && year <= 2025;
  }

  validateSize() {
    const pattern = /^[0-9]+$/;
    return pattern.test(String(this.state.size));
  }

  validatePrice() {
    const pattern = /^[0-9]+$/;
    return pattern.test(String(this.state.price));
  }

  validateQuantity() {
    const pattern = /^[0-9]+$/;
    return pattern.test(String(this.state.quantity));
  }

  validateImages() {
    const pattern = /^[A-Za-z,;:/_.!?@ \-0-9\s]+$/;
    return pattern.test(String(this.state.images));
  }

