  validate() {
    return {
      productName: this.validateProductName(),
      productDescription: this.validateProductDescription(),
      year: this.validateYear(),
      size: this.validateSize(),
      price: this.validatePrice(),
      quantity: this.validateQuantity(),
      images: this.validateImages(),
    };
  }
  validate() {
    if (this.validate()) {
      return true;
    }
  }
