import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import {
  ACCESS_LEVEL_GUEST,
  ACCESS_LEVEL_ADMIN,
  SERVER_HOST,
} from "../config/global_constants";

export default class Product extends Component {
