import React, { Component } from "react";
import Form from "react-bootstrap/Form";
import { Redirect, Link } from "react-router-dom";
import axios from "axios";

import LinkInClass from "../components/LinkInClass";
import CarTableRow from "./CarTableRow";

import {
  ACCESS_LEVEL_NORMAL_USER,
  SERVER_HOST,
} from "../config/global_constants";

export default class EditCar extends Component {
  constructor(props) {
    super(props);

    this.state = {
      model: ``,
      colour: ``,
      year: ``,
      price: ``,
      redirectToDisplayAllCars:
      localStorage.accessLevel < ACCESS_LEVEL_NORMAL_USER,
      wasSubmittedAtLeastOnce: false,
    };
  }

  componentDidMount() {
    this.inputToFocus.focus();

    axios
      .get(`${SERVER_HOST}/cars/${this.props.match.params.id}`, {
        headers: { authorization: localStorage.token },
      })
      .then((res) => {
        console.log(res);
        this.setState({
          model: res.data.model,
          colour: res.data.colour,
          year: res.data.year,
          price: res.data.price,
          images: res.data.images,

        });console.log(this.stateimages);
      })
      .catch((err) => {
        // do nothing
      });


      // this.props.car.photos.map((photo) => {
      //   return axios
      //     .get(`${SERVER_HOST}/cars/photo/${photo.filename}`)
      //     .then((res) => {
      //       document.getElementById(
      //         photo._id
      //       ).src = `data:;base64,${res.data.image}`;
      //     })
      //     .catch((err) => {
      //       // do nothing
      //     });
      // });

  }

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleSubmit = (e) => {
    e.preventDefault();

    const carObject = {
      model: this.state.model,
      colour: this.state.colour,
      year: this.state.year,
      price: this.state.price,
      images: this.state.images,
    };

    axios
      .put(`${SERVER_HOST}/cars/${this.props.match.params.id}`, carObject, {
        headers: { authorization: localStorage.token },
      })
      .then((res) => {
        this.setState({ redirectToDisplayAllCars: true });
      })
      .catch((err) => {
        this.setState({ wasSubmittedAtLeastOnce: true });
      });
  };

  render() {
    let errorMessage = "";
    if (this.state.wasSubmittedAtLeastOnce) {
      errorMessage = (
        <div className="error">
          Error: All fields must be filled in
          <br />
        </div>
      );
    }

    return (
      <div className="form-container">
        {this.state.redirectToDisplayAllCars ? (
          <Redirect to="/DisplayAllCars" />
        ) : null}

        {errorMessage}

        <Form>
          <Form.Group controlId="model">
            <Form.Label>Model</Form.Label>
            <Form.Control
              ref={(input) => {
                this.inputToFocus = input;
              }}
              type="text"
              name="model"
              value={this.state.model}
              onChange={this.handleChange}
            />
          </Form.Group>
          <Form.Group controlId="colour">
            <Form.Label>Colour</Form.Label>
            <Form.Control
              type="text"
              name="colour"
              value={this.state.colour}
              onChange={this.handleChange}
            />
          </Form.Group>
          {/* <Form.Group controlId="year">
            <Form.Label>Year</Form.Label>
            <Form.Control
              type="text"
              name="year"
              value={this.state.year}
              onChange={this.handleChange}
            />
          </Form.Group> */}
          <Form.Group controlId="price">
            <Form.Label>Price</Form.Label>
            <Form.Control
              type="text"
              name="price"
              value={this.state.price}
              onChange={this.handleChange}
            />
          </Form.Group>
          {/* {this.props.cars.map((car) => (
            <CarTableRow key={car._id} car={car} />
          ))} */}

          {/* {this.props.car.photos.map((photo) => (
            <img
              className="product-image"
              key={photo._id}
              id={photo._id}
              alt="Image"
            />
          ))} */}

          <Form.Group controlId="photos">
            <Form.Label>Photos</Form.Label>
            <Form.Control
              type="file"
              multiple
              onChange={this.handleFileChange}
            />
          </Form.Group>{" "}
          <br />
          <br />
          <LinkInClass
            value="Update"
            className="green-button"
            onClick={this.handleSubmit}
          />
          <Link className="red-button" to={"/DisplayAllCars"}>
            Cancel
          </Link>
        </Form>
      </div>
    );
  }
}
