export default class DisplayAllProducts extends Component {
  constructor(props) {
    super(props);

    this.state = {
      products: [],
      selectedProducts: [],
      releaseYears: [],
      selectedYears: "All Years",
      sizes: [],
      selectedSizes:"All Sizes",
      isRecords: true,
      customersCount: 0,
      users: [],
      customers: [
        {
          id: 1,
          name: "Scott",
          phone: "234234",
          address: { city: "London" },
          photo: "https://picsum.photos/id/1010/60",
        },
        {
          id: 2,
          name: "Jones",
          phone: "567567",
          address: { city: "Praga" },
          photo: "https://picsum.photos/id/1011/60",
        },
        {
          id: 3,
          name: "Smith",
          phone: null,
          address: { city: "Sofia" },
          photo: "https://picsum.photos/id/1012/60",
