        {
          id: 5,
          name: "Alan",
          phone: "3453466",
          address: { city: "Castlebalyney" },
          photo: "https://picsum.photos/id/1014/60",
        },
      ],
    };
  }

  componentDidMount() {
    axios.get(`${SERVER_HOST}/products`).then((res) => {
      console.log(res.data);

      /*Filter by Year*/

      let releaseYears = res.data.map((product) => product.year);
      let uniqueYears = [...new Set(releaseYears)].sort();
      uniqueYears.unshift("All Years");
      console.log("uniqueYeasr", uniqueYears);
      /*Filter by Size*/

      let sizes = res.data.map((product) => product.size);
      let uniqueSizes = [...new Set(sizes)].sort();
      uniqueSizes.unshift("All Sizes");
      console.log("uniqueSizes",uniqueSizes);

      if (res.data) {
        if (res.data.errorMessage) {
          console.log(res.data.errorMessage);
        } else {
          console.log("Records read");
          this.setState({
            products: res.data,
            selectedProducts: res.data,
            releaseYears: uniqueYears,
            sizes: uniqueSizes,
          });
        }
      } else {
        console.log("Record not found");
      }
      // this.setState({ products: res.data });
    });
  }

  filterYear = (e) => {
    if (e.target.value === this.state.selectedYears) {
      this.setState({ selectedProducts: this.state.products });
    } else {
      this.setState({
        selectedProducts: this.state.products.filter(
          (product) => product.releaseYear.toString() === e.target.value
        ),
      });
    }
  };

  filterSizes = (e) => {
    if (e.target.value === this.state.selectedSizes) {
      this.setState({ selectedProducts: this.state.albums });
    } else {
      this.setState({
        selectedProducts: this.state.products.filter(
          (product) => product.s.toString() === e.target.value
        ),
      });
    }
  };



  handleSearch = (searchTag) => {
    console.log(searchTag);
    this.setState({
      selectedProducts: this.state.products.filter(
        (product) =>
          product.productName.toLowerCase().includes(searchTag.toLowerCase()) ||
          product.productDescription
            .toLowerCase()
            .includes(searchTag.toLowerCase()) ||
          product.year.toLowerCase().includes(searchTag.toString())
      ),
    });
  };

  render() {
    return (
      <>
        <Header />

        <div className="header__search">
          <input
            className="header__searchInput"
            type="text"
            placeholder="Search by prduct name or size..."
            id="search"
            onChange={() =>
              this.handleSearch(document.getElementById("search").value)
            }
          />
          <SearchIcon className="header__searchIcon" />
        </div>
        <div className="Filters">
          <select name="filters" id="filterList" onChange={this.filterYear}>
            {this.state.releaseYears.map((releaseYear) => (
              <option key={releaseYear} value={releaseYear}>
                {releaseYear}
              </option>
            ))}
          </select>{" "}
          <select name="filters" id="filterList" onChange={this.filterSizes}>
            {this.state.sizes.map((size) => (
              <option key={size} value={size}>
                {size}
              </option>
            ))}
          </select>{" "}
        </div>

        {/* <ShoppingCart x="10" /> */}

        <div className="container1">
          <ProductTable products={this.state.selectedProducts} />
          {/* {this.state.isRecords ? (
            <ProductTable products={this.state.products} />
          ) : (
            <Redirect to="/NoRecordsToDisplay" />
          )} */}
        </div>

        <Footer />
      </>
    );
  }
}

