import React, { Component } from "react";
import Product from "./Product";

export default class ShoppingCart extends Component {
  constructor(props) {
    console.log("constrtuctor - Shopping cart");
    super(props);
    this.state = {
      products: [
        { id: 1, productName: "Iphone", price: 500, quantity: 0 },
        { id: 2, productName: "Samsung", price: 600, quantity: 0 },
        { id: 3, productName: "iPad", price: 400, quantity: 0 },
        { id: 4, productName: "Xbox", price: 700, quantity: 0 },
        { id: 5, productName: "Psp", price: 200, quantity: 0 },
        { id: 6, productName: "Dogee", price: 100, quantity: 0 },
      ],
    };
  }

  componentDidMount() {
    console.log("componentDid - Shopping cart");
  }
  componentDidUpdate(prevProps, prevState) {
    console.log(
      "componentUpdate ",
      prevProps,
      prevState,
      this.props,
      this.state
    );
  }

  // Increment and decrement functions taking the product
  //state from Product component
  handleIncrement = (product, max) => {
    //copy the product array
    let allProducts = [...this.state.products];
    //get the index of any product
    let index = allProducts.indexOf(product);
    if (allProducts[index].quantity < max) {
      allProducts[index].quantity++;
      //update the state with the changed array allProducts
      this.setState({ products: allProducts });
    }

    // console.log("handleIncrement", product);
    // console.log(allProducts[index]);
  };
  handleDecrement = (product, min) => {
    let allProducts = [...this.state.products];
    let index = allProducts.indexOf(product);
    if (allProducts[index].quantity > min) {
      allProducts[index].quantity--;

      this.setState({ products: allProducts });
    }

    // console.log("handleDecrement", product);
  };
  //delete product
  handelDelete = (product) => {
    //get the product by index
    let allProducts = [...this.state.products];
    let index = allProducts.indexOf(product);
    if (window.confirm("Are you sure to Delete the product?")) {
      //delete the product with
      allProducts.splice(index, 1);
      //changing the state with the deleted product
      this.setState({ products: allProducts });
    }
  };

  render() {
    console.log("render - Shopping cart");
    return (
      <div className="containr-fluid">
        <h4>ShoppingCart</h4>
        <div className="row">
          {this.state.products.map((prod) => {
            return (
              <Product
                key={prod.id}
                product={prod}
                onIncrement={this.handleIncrement}
                onDecrement={this.handleDecrement}
                onDelete={this.handelDelete}
              >
                <button className="btn btn-primary">Buy now</button>
              </Product>
            );
          })}
        </div>
      </div>
    );
  }
}
