import React, { Component } from "react";
import { Link } from "react-router-dom";
import { MdDeleteForever } from "react-icons/md";
import { FaEdit } from "react-icons/fa";
// import BuyProduct from "./BuyProduct";
import axios from "axios";

import {
  ACCESS_LEVEL_GUEST,
  ACCESS_LEVEL_ADMIN,
  SERVER_HOST,
} from "../config/global_constants";

export default class ProductTableRow extends Component {
  constructor(props) {
    super(props);
   // console.log("Constructor - product");
    this.state = {
      product: this.props.product,
    };
  }

  componentDidMount() {
  //  console.log(this.props.product.photos[0].filename);

    return axios

      .get(
        `${SERVER_HOST}/products/photo/${this.props.product.photos[0].filename}`
      )

      .then((res) => {
        console.log(res);
        let img = document.getElementById(this.props.product.photos[0]._id);
        img.src = `data:;base64,${res.data.image}`;
      })
      .catch((err) => {
        // do nothing
      });
  }
  handleOnClick() {

  }

  render() {
    return (
      <div className="product">
        <div className="produt_info" >
          <strong>{this.props.product.productName}</strong>
          <p> {this.props.product.productDescription}</p>
          <p>{this.props.product.year}</p>

          <p className="product__price">
            <small>€</small>
            <strong>{this.props.product.price}</strong>
          </p>
        </div>

        <img id={this.props.product.photos[0]._id} alt="error" />

        {/* {this.props.car.photos.map((photo) => (
          <img
            className="product-image"
            key={photo._id}
            id={photo._id}
            alt=""
          />
        ))} */}
        <span className="badge">{this.props.product.quantity} </span>

        <div className="btn-group">
          <button
            className="btn btn-outline-success"
            onClick={() => {
              this.props.onIncrement(this.state.product, 10);
            }}
          >
            +
          </button>
          <button
            className="btn btn-outline-danger"
            onClick={() => {
              this.props.onDecrement(this.state.product, 0);
            }}
          >
            -
          </button>
        </div>

        <button>Add to basket </button>
        <div className="buttons-bottom">
          <div className="green-button">
            {localStorage.accessLevel > ACCESS_LEVEL_GUEST ? (
              <Link to={"/EditProduct/" + this.props.product._id}>
                <FaEdit />
              </Link>
            ) : null}
          </div>
          <div className="red-button">
            {localStorage.accessLevel >= ACCESS_LEVEL_ADMIN ? (
              <Link to={"/DeleteProduct/" + this.props.product._id}>
                <MdDeleteForever />
              </Link>
            ) : null}
          </div>
        </div>

        {/* <BuyProduct /> */}
      </div>
    );
  }
}

