import React, { Component } from "react";
import { Redirect, Link } from "react-router-dom";
import axios from "axios";

import LinkInClass from "../components/LinkInClass";
import { SERVER_HOST } from "../config/global_constants";

export default class Login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      email: "",
      password: "",
      isLoggedIn: false,
      wasSubmittedAtLeastOnce: false,
    };
  }

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleSubmit = (e) => {
    axios
      .post(
        `${SERVER_HOST}/users/login/${this.state.email}/${this.state.password}`
      )
      .then((res) => {
        localStorage.name = res.data.name;
        localStorage.accessLevel = res.data.accessLevel;
        localStorage.profilePhoto = res.data.profilePhoto;
        localStorage.token = res.data.token;

        this.setState({ isLoggedIn: true });
      })
      .catch((err) => {
        console.log("err.response.status", err.response.status); // the error message's status code (e.g. 401)
        console.log("err.response.statusText", err.response.statusText); // the default error message text that is associated with the status code (e.g. "Unauthorized")
        console.log("err.response.data", err.response.data); // the error message's text. This can be custom defined on the server-side.

        this.setState({ wasSubmittedAtLeastOnce: true });
      });
  };

  render() {
    let errorMessage = "";
    if (this.state.wasSubmittedAtLeastOnce) {
      errorMessage = (
        <div className="error">
          Login Details are incorrect
          <br />
        </div>
      );
    }

    return (
      <div className="col-lg-9">
        <form
          className="form-container"
          noValidate={true}
          id="loginOrRegistrationForm"
        >
          <h2 className="m-1 p-2 border-bottom">Login</h2>
          {/* Email Start */}
          <div className="form-group form">
            <label className="col-lg-4">Email</label>
            <input
              type="email"
              name="email"
              className="form-control"
              placeholder="Enter your email"
              autoComplete="email"
              value={this.state.email}
              onChange={this.handleChange}
            />
          </div>
          {/* Email END */}

          {/* password Start */}
          <div className="form-group form">
            <label className="col-lg-4">Email</label>
            <input
              type="password"
              name="password"
              className="form-control"
              placeholder="Enter your password"
              autoComplete="email"
              value={this.state.email}
              onChange={this.handleChange}
            />
          </div>
          {/* passwrod END */}
          <LinkInClass
            value="Login"
            className="btn bg-primary"
            onClick={this.handleSubmit}
          />
          <Link className="btn bg-" to={"/DisplayAllCars"}>
            Cancel
          </Link>
          <br></br>
          <br></br>

          {this.state.isLoggedIn ? <Redirect to="/DisplayAllCars" /> : null}

          {errorMessage}

          <input
            type="password"
            name="password"
            placeholder="Enter Password"
            autoComplete="email"
            value={this.state.email}
            onChange={this.handleChange}
          />
          <br />

          <input
            type="password"
            name="password"
            placeholder="Password"
            autoComplete="password"
            value={this.state.password}
            onChange={this.handleChange}
          />
          <br />
          <br />

          <LinkInClass
            value="Login"
            className="green-button"
            onClick={this.handleSubmit}
          />
          <Link className="red-button" to={"/DisplayAllCars"}>
            Cancel
          </Link>
        </form>
      </div>
    );
  }
}
