import React, { Component } from "react";
import { Link } from "react-router-dom";
import { MdDeleteForever } from "react-icons/md";
import { FaEdit } from "react-icons/fa";
// import BuyProduct from "./BuyProduct";
import axios from "axios";

import {
  ACCESS_LEVEL_GUEST,
  ACCESS_LEVEL_ADMIN,
  SERVER_HOST,
} from "../config/global_constants";

export default class CarTableRow extends Component {
  componentDidMount() {
    console.log(this.props.car.photos[0].filename);

    return axios

      .get(`${SERVER_HOST}/cars/photo/${this.props.car.photos[0].filename}`)

      .then((res) => {
        console.log(res);
        document.getElementById(
          this.props.car.photos._id
          // photo._id
        ).src = `data:;base64,${res.data.image}`;
        console.log(res.data.image);
      })
      .catch((err) => {
        // do nothing
      });
  }

  render() {
    return (
      <div className="product">
        <div className="produt_info">
          <strong>{this.props.car.model}</strong>
          <p> {this.props.car.colour}</p>
          <p>{this.props.car.year}</p>

          <p className="product__price">
            <small>â‚¬</small>
            <strong>{this.props.car.price}</strong>
          </p>
          {/* <div className="product__rating">
             {Array(rating)
                    .fill()
                    .map(() => (
                      <p>*</p>
                    ))} 
          </div> */}
        </div>
        {/* <img src={cust.photo} alt="Customer" /> */}

        <img id={this.props.car.photos} alt="image" />

        {/* {this.props.car.photos.map((photo) => (
          <img
            className="product-image"
            key={photo._id}
            id={photo._id}
            alt=""
          />
        ))} */}
        {/* {this.props.car.photos.map((photo) => (
            <img
              className="product-image"
              key={photo._id}
              id={photo._id}
              alt=""
            />
          ))} */}

        <button>Add to basket </button>
        <div className="buttons-bottom">
          <div className="green-button">
            {localStorage.accessLevel > ACCESS_LEVEL_GUEST ? (
              <Link to={"/EditCar/" + this.props.car._id}>
                <FaEdit />
              </Link>
            ) : null}
          </div>
          <div className="red-button">
            {localStorage.accessLevel >= ACCESS_LEVEL_ADMIN ? (
              <Link to={"/DeleteCar/" + this.props.car._id}>
                <MdDeleteForever />
              </Link>
            ) : null}
          </div>
        </div>

        {/* <BuyProduct /> */}
      </div>
    );
  }
}
